import React from "react";
import {connect} from 'react-redux';

export class dashboard extends React.Component {
  constructor(props){
    super(props);
    this.total_manus = this.total_manus.bind(this);
    this.state ={
      total_xps: 0,
      total_gseries: 0,
      total_vostro: 0,
      total_inspiron: 0,
      count: 0
    }
  }

  total_manus(){
  if(this.state.count<3){
    for(var i = 0; i<6; i++){
      for(var j = 0; j<336; j++){
        console.log(this.props.optimized[i].step3[j]);
        if(this.props.optimized[i].step3[j] == "XPS"){
          this.setState({total_xps: this.state.total_xps+1});
        }
        if(this.props.optimized[i].step3[j] == "Vostro"){
          this.setState({total_xps: this.state.total_vostro+1});
        }
        if(this.props.optimized[i].step3[j] == "Inspiron"){
          this.setState({total_xps: this.state.total_inspiron+1});
        }
        if(this.props.optimized[i].step3[j] == "GSeries"){
          this.setState({total_xps: this.state.total_gseries+1});
        }
      }
    }
    
    this.setState({total_xps: this.state.total_xps/2});
    this.setState({total_xps: this.state.total_inspiron/2});
    this.setState({count: this.state.count+1});
  }
}

  render() {
    if(!this.props.optimized)return null;
    this.total_manus();
    return (
      <div id="dashboard" className="text-center">
        <div className="container">
          <div className="section-title">
            <h2>Dashboard</h2>
            <p>
              All your statistics at one glance
            </p>
          </div>
          <div className="row">
            {this.props.data.dat
              ? this.props.data.dat.map((d, i) => (
                <div>
                  <div  key={`${d.name}-${i}`} className="col-md-3">
                    {" "}
                    <i className="stat">{d.icon} 
                      <div className="service-desc">
                        <h3>{d.name}</h3>
                      </div>
                    </i>
                  </div>
                </div>
                  
                  
                  
                ))
              : "loading"}
              
              <div className="col-md-3">
              
              <i className="stat">{this.state.total_gseries + this.state.total_inspiron + this.state.total_vostro + this.state.total_xps}
                <div className="service-desc">
                  <h3>Total Backlogs for the week</h3>
                </div>
              </i>
            </div>
          </div>

          <div className="row">
                <div>
                  <div className="col-md-6">
                    
                    <i className="grph"
                    ><iframe width="500" height="270" src="https://datastudio.google.com/embed/reporting/a3624847-e9e3-40c3-bb22-a599cd01a4c3/page/Rn0rB" frameborder="0" allowFullScreen></iframe>
                      <div className="service-desc">
                        <h3>Sales statistics(In thousands)</h3>
                      </div>
                    </i>
                  </div>
                  
                  </div>
                  
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  // console.log(state);
  return {
   optimized : state.optimized[0]
  };
};

export default connect(mapStateToProps,null) (dashboard);
//export default dashboard;
